mapper.select:
    SELECT NUM, COMPANY_CODE, COMPANY_NAME, LOGO, COMPANY_CLASSIFICATION, AVG_SAL, HEADCOUNT, INPUT_DATE
	FROM (
	SELECT ROW_NUMBER() OVER (ORDER BY COMPANY_CODE) AS NUM,
	COMPANY_CODE, COMPANY_NAME, LOGO, COMPANY_CLASSIFICATION, AVG_SAL, HEADCOUNT, INPUT_DATE FROM COMPANYINFO


			AND COMPANY_NAME LIKE '%' || #{searchDataValue} || '%'


			AND AVG_SAL >= #{avgSal}


            AND COMPANY_CLASSIFICATION = #{companyClassification} 


        )


				AND NUM >= #{startRow} AND NUM <= #{endRow}


		ORDER BY company_name

		SELECT CEIL(COUNT(*)/5)
		FROM COMPANYINFO


				AND COMPANY_NAME LIKE '%' || #{searchDataValue} || '%'


				AND AVG_SAL >= #{avgSal}


            	AND COMPANY_CLASSIFICATION = #{companyClassification}

SELECT COMPANY_CODE,BASE_DATE,HISTORY_CONTENT
FROM HISTORY
WHERE COMPANY_CODE = #{companyCode}

SELECT COMPANY_CODE,CATEGORY,WELFARE_CONTENT
FROM WELFARE
WHERE COMPANY_CODE = #{companyCode}

SELECT COMPANY_CODE,CATEGORY
FROM WELFARE
