mapper.select:
select resume_num as resumeNum, title, input_date as inputDate
from resume 
where user_id=#{user_id}

    	select count(*) as count
		from resume 
		where user_id=#{user_id}

mapper.insert:
INSERT INTO skill (RESUME_NUM, SKILL_CODE)
SELECT A.* FROM (

        SELECT #{skill.resumeNum} AS RESUME_NUM, sl.SKILL_CODE
        FROM skill_list sl
        WHERE sl.SKILL_NAME = #{skill.skillName}

) A

INSERT INTO education ( RESUME_NUM, SCHOOL_CLASSIFICATION, SCHOOL_NAME, ADMISSION_DATE, GRADUATION_DATE, GRADUATION_STATE, MAJOR, GRADES, TOTAL_SCORE ) SELECT A.* FROM (
SELECT #{edu.resumeNum}, #{edu.schoolClassification}, #{edu.schoolName}, TO_DATE(TO_CHAR(#{edu.admissionDate}, 'YYYY-MM-DD'), 'YYYY-MM-DD'), TO_DATE(TO_CHAR(#{edu.graduationDate}, 'YYYY-MM-DD'), 'YYYY-MM-DD'), #{edu.graduationState}, #{edu.major}, #{edu.grades}, #{edu.totalScore} FROM DUAL
) A
